import { Switch, Route, Link, useLocation } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/theme-provider";
import { ThemeToggle } from "@/components/theme-toggle";
import { Button } from "@/components/ui/button";
import { BarChart3, Users, Sparkles, Network } from "lucide-react";
import NotFound from "@/pages/not-found";
import Dashboard from "@/pages/dashboard";
import Animals from "@/pages/animals";
import Breeding from "@/pages/breeding";
import Lineage from "@/pages/lineage";

function Navigation() {
  const [location] = useLocation();

  const navItems = [
    { path: "/", label: "Dashboard", icon: BarChart3, shortLabel: "Dashboard" },
    { path: "/animals", label: "Animals", icon: Users, shortLabel: "Animals" },
    { path: "/breeding", label: "Breeding", icon: Sparkles, shortLabel: "Breeding" },
    { path: "/lineage", label: "Herd Data", icon: Network, shortLabel: "Herds" },
  ];

  return (
    <>
      {/* Desktop/Tablet Header Navigation */}
      <header className="hidden md:block sticky top-0 z-50 border-b bg-card">
        <div className="max-w-7xl mx-auto px-6 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-8">
              <Link href="/">
                <div className="flex items-center gap-2 hover-elevate active-elevate-2 rounded-lg px-3 py-2 -ml-3 cursor-pointer">
                  <div className="w-8 h-8 rounded-lg bg-primary flex items-center justify-center">
                    <Sparkles className="h-5 w-5 text-primary-foreground" />
                  </div>
                  <span className="text-xl font-bold">BreedWise</span>
                </div>
              </Link>
              <nav className="flex items-center gap-1">
                {navItems.map((item) => {
                  const Icon = item.icon;
                  const isActive = location === item.path;
                  return (
                    <Button
                      key={item.path}
                      variant={isActive ? "secondary" : "ghost"}
                      className="gap-2"
                      asChild
                      data-testid={`link-${item.label.toLowerCase()}`}
                    >
                      <Link href={item.path}>
                        <Icon className="h-4 w-4" />
                        {item.label}
                      </Link>
                    </Button>
                  );
                })}
              </nav>
            </div>
            <ThemeToggle />
          </div>
        </div>
      </header>

      {/* Mobile Header - Logo Only */}
      <header className="md:hidden sticky top-0 z-50 border-b bg-card">
        <div className="px-4 py-3">
          <div className="flex items-center justify-between">
            <Link href="/">
              <div className="flex items-center gap-2">
                <div className="w-7 h-7 rounded-lg bg-primary flex items-center justify-center">
                  <Sparkles className="h-4 w-4 text-primary-foreground" />
                </div>
                <span className="text-lg font-bold">BreedWise</span>
              </div>
            </Link>
            <ThemeToggle />
          </div>
        </div>
      </header>

      {/* Mobile Bottom Navigation Bar - Always Visible */}
      <nav className="md:hidden fixed bottom-0 left-0 right-0 z-[100] border-t bg-card shadow-lg" style={{ paddingBottom: 'env(safe-area-inset-bottom, 0px)' }}>
        <div className="grid grid-cols-4 gap-1 px-2 py-3">
          {navItems.map((item) => {
            const Icon = item.icon;
            const isActive = location === item.path;
            return (
              <Link
                key={item.path}
                href={item.path}
                data-testid={`mobile-nav-${item.label.toLowerCase()}`}
              >
                <div
                  className={`flex flex-col items-center justify-center gap-1 py-3 px-1 rounded-xl transition-colors ${
                    isActive
                      ? "bg-primary text-primary-foreground shadow-md"
                      : "text-muted-foreground active:bg-muted"
                  }`}
                >
                  <Icon className="h-6 w-6" />
                  <span className="text-xs font-semibold">{item.shortLabel}</span>
                </div>
              </Link>
            );
          })}
        </div>
      </nav>
    </>
  );
}

function Router() {
  return (
    <Switch>
      <Route path="/" component={Dashboard} />
      <Route path="/animals" component={Animals} />
      <Route path="/breeding" component={Breeding} />
      <Route path="/lineage" component={Lineage} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider defaultTheme="light">
        <TooltipProvider>
          <div className="min-h-screen flex flex-col bg-background">
            <Navigation />
            <main className="flex-1 pb-24 md:pb-0">
              <Router />
            </main>
          </div>
          <Toaster />
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, decimal, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export const herds = pgTable("herds", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export const insertHerdSchema = createInsertSchema(herds).omit({
  id: true,
  createdAt: true,
}).extend({
  description: z.string().optional(),
});

export type InsertHerd = z.infer<typeof insertHerdSchema>;
export type Herd = typeof herds.$inferSelect;

export const animals = pgTable("animals", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  species: text("species").notNull(),
  sex: text("sex").notNull(),
  healthNotes: text("health_notes"),
  hornSize: decimal("horn_size", { precision: 5, scale: 2 }),
  sireId: varchar("sire_id"),
  damId: varchar("dam_id"),
  herdId: varchar("herd_id"),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export const insertAnimalSchema = createInsertSchema(animals).omit({
  id: true,
  createdAt: true,
}).extend({
  sex: z.enum(["Male", "Female"]),
  healthNotes: z.string().optional(),
  hornSize: z.number().min(0).optional(),
  sireId: z.string().optional(),
  damId: z.string().optional(),
  herdId: z.string().optional(),
});

export type InsertAnimal = z.infer<typeof insertAnimalSchema>;
export type Animal = typeof animals.$inferSelect;

export const breedingRecommendations = pgTable("breeding_recommendations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  animalIds: text("animal_ids").notNull(),
  aiExplanation: text("ai_explanation"),
  confidence: decimal("confidence", { precision: 3, scale: 2 }),
  createdAt: timestamp("created_at").notNull().default(sql`now()`),
});

export const insertBreedingRecommendationSchema = createInsertSchema(breedingRecommendations).omit({
  id: true,
  createdAt: true,
});

export type InsertBreedingRecommendation = z.infer<typeof insertBreedingRecommendationSchema>;
export type BreedingRecommendation = typeof breedingRecommendations.$inferSelect;

export interface HerdAnalysis {
  predictedOutcomes: {
    estimatedOffspringCount: number;
    averageHornSize?: number;
    traitStrength: string;
    geneticDiversity: string;
  };
  confidence: number;
  explanation: string;
  breedingStrategy: string;
  hasRelatedAnimals: boolean;
  relatedAnimalsWarning?: string;
}

export interface HerdBreedingRecommendation {
  herdAnimals: Animal[];
  analysis: HerdAnalysis;
  herdScore: number;
}

client/src/components/ui/toggle-group.ts.content:
"use client"

import * as React from "react"
import * as ToggleGroupPrimitive from "@radix-ui/react-toggle-group"
import { type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import { toggleVariants } from "@/components/ui/toggle"

const ToggleGroupContext = React.createContext<
  VariantProps<typeof toggleVariants>
>({
  size: "default",
  variant: "default",
})

const ToggleGroup = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, children, ...props }, ref) => (
  <ToggleGroupPrimitive.Root
    ref={ref}
    className={cn("flex items-center justify-center gap-1", className)}
    {...props}
  >
    <ToggleGroupContext.Provider value={{ variant, size }}>
      {children}
    </ToggleGroupContext.Provider>
  </ToggleGroupPrimitive.Root>
))

ToggleGroup.displayName = ToggleGroupPrimitive.Root.displayName

const ToggleGroupItem = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Item> &
    VariantProps<typeof toggleVariants>
>(({ className, children, variant, size, ...props }, ref) => {
  const context = React.useContext(ToggleGroupContext)

  return (
    <ToggleGroupPrimitive.Item
      ref={ref}
      className={cn(
        toggleVariants({
          variant: context.variant || variant,
          size: context.size || size,
        }),
        className
      )}
      {...props}
    >
      {children}
    </ToggleGroupPrimitive.Item>
  )
})

ToggleGroupItem.displayName = ToggleGroupPrimitive.Item.displayName

export { ToggleGroup, ToggleGroupItem }

